class Ingredient extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ingredients: 1,
    };
  }

  render() {
    const ingredient = this.state.ingredients.map((step, num) => {
      const desc = num ? 'Ingredient #' + num : 'No ingredients added.';
      return (
        <h3>{desc}</h3>
      );
    });
    return;
  };
}

export default App;
