import React from 'react';
import logo from './little-chef.gif';
import './App.css';

function Search() {
  return (
    <div className="center">
      <button className="search-button hvr-bounce-in noSelect" type="button">Search!</button>
    </div>
  );
}

function Ingredient(props) {
  return(
    <div className="center">
      <h3>Type in your ingredients, separated by comma.</h3>
      <input
        type="text"
        ingredients={props.ingredients}
        onKeyPress={props.onKeyPress}
        onChange={props.onChange}
        value={props.response}
      />
    </div>
  );
}

class Kitchen extends React.Component {
  renderSearch() {
    return <Search />;
  }

  renderIngredients() {
    return <Ingredient
      ingredients={this.props.ingredients}
      onKeyPress={(event) => this.props.onKeyPress}
      onChange={(event) => this.props.onChange}
      response={this.props.response}
    />;
  }

  renderHeader() {
    return (
      <div>
        <h1>Search recipes by ingredients.</h1>
      </div>
    );
  }

  renderShoppingList() {
    return (
      this.props.ingredients.length === 0 ?
        <h3>What have you got today?</h3> :
        <h3>{this.props.ingredients}</h3>
    );
  }

  render() {
    return (
      <div>
        <div className="topHeader">
          {this.renderHeader()}
          {this.renderShoppingList()}
        </div>
        <div>
          {this.props.ingredients.length === 0 ? "" : this.renderSearch()}
        </div>
        <div>
          {this.renderIngredients()}
        </div>
      </div>
    );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ingredients: [],
      response: "",
    }
  }

  inputKeyPress = (event) => {
    if (event.key === "Enter") {
      const newIngredients = [event.target.value];
      this.setState({
        ingredients: newIngredients,
        // response: "",
      });
    }
  }

  updateResponse = (event) => {
    this.setState({
      // response: event.target.value,
    });
  }

  render() {
    return(
      <div className="App">
        <header className="App-header">
          <h1>Little Chef</h1>
          <img src={logo} className="App-logo" alt="logo" />
          <p>Welcome to <b>Little Chef</b>! Currently under construction.</p>
          <p>Built for HackHarvard 2018.</p>
          <div className="arrow"></div>
        </header>
        <div className="main">
          <Kitchen
            ingredients={this.state.ingredients}
            onKeyPress={(event) => this.state.inputKeyPress(event)}
            onChange={(event) => this.state.updateResponse(event)}
            response={this.state.response}
          />
          <div className="ingredient-info">
            {/* <ol>{moves}</ol> */}
          </div>
        </div>
      </div>
    );
  }
}

// ========================================

export default App;
