import React from 'react';
import logo from './little-chef.gif';
import './App.css';

function Search() {
  return (
    <div className="center">
      <button className="search-button hvr-bounce-in noSelect" type="button">Search!</button>
    </div>
  );
}

class Ingredient extends React.Component {
  // updateResponse = (event) => {
  //     this.setState({ response: event.target.value });
  // }
  //
  // inputKeyPress = (event) => {
  //     if (event.key === "Enter") {
  //         const answer = (this.state.response);
  //         this.props.
  //     }
  // }

  handleIngredients() {
    return(
      <div className="center">
        {/* <h3>{desc}</h3> */}
        <h3>Type in your ingredients, separated by comma.</h3>
        <input
          type="text"
          // onKeyPress={this.inputKeyPress}
          // onChange={this.updateResponse}
          // value={this.state.response}
        />
        {/* <button className="minus-button hvr-bounce-out noSelect" onClick={this.props.onMinus} type="button">-</button> */}
      </div>
    );
  }

  render() {
    return (
      <div>
        {this.handleIngredients()}
      </div>
    );
  }
}

class Kitchen extends React.Component {
  renderSearch() {
    return <Search />;
  }

  renderIngredients() {
    return <Ingredient
      ingredients={this.props.ingredients}
    />;
  }

  renderHeader() {
    const emptyKitchen =
      <div>
        <h1>Search recipes by ingredients.</h1>
        {/* <h2>You've got an empty kitchen!</h2> */}
      </div>;
    return (
      (this.props.ingredients.length === 0) ?
      <div>{emptyKitchen}</div> :
        ((this.props.ingredients.length === 1) ?
        <h1>{this.props.ingredients.length} ingredient.</h1> :
        <h1>{this.props.ingredients.length} ingredients.</h1>)
    );
  }

  renderShoppingList() {
    return (
      this.props.ingredients.length === 0 ?
        <h3>What have you got today?</h3> :
        <h3>{this.props.ingredients}</h3>
    );
  }

  render() {
    return (
      <div>
        <div className="topHeader">
          {this.renderHeader()}
          {this.renderShoppingList()}
        </div>
        <div>
          {this.props.ingredients.length === 0 ? "" : this.renderSearch()}
        </div>
        <div>
          {this.renderIngredients()}
        </div>
      </div>
    );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ingredients: [],
    }
  }

  render() {
    return(
      <div className="App">
        <header className="App-header">
          <h1>Little Chef</h1>
          <img src={logo} className="App-logo" alt="logo" />
          <p>Welcome to <b>Little Chef</b>! Currently under construction.</p>
          <p>Built for HackHarvard 2018.</p>
          <div className="arrow"></div>
        </header>
        <div className="main">
          <Kitchen
            ingredients={this.state.ingredients}
          />
          <div className="ingredient-info">
            {/* <ol>{moves}</ol> */}
          </div>
        </div>
      </div>
    );
  }
}

// ========================================

export default App;
