import React from 'react';
import logo from './little-chef.gif';
import './App.css';

class Ingredient extends React.Component {
  render() {
    return (
      <div>
        {/* <h3>{desc}</h3> */}
        <input type="text"/>
      </div>
    );
  }
}

class Kitchen extends React.Component {
  renderIngredient(i) {
    return <Ingredient />;
  }

  render() {
    const emptyKitchen = "No ingredients added. You've got an empty kitchen!";
    return (
      (this.props.ingredients === 0) ?
        <h1>{emptyKitchen}</h1> :
        <h1>Not empty!!</h1>
    );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ingredients: 0,
    }
  }

  render() {
    return(
      <div className="App">
        <header className="App-header">
          <h1>Little Chef</h1>
          <img src={logo} className="App-logo" alt="logo" />
          <p>Welcome to <b>Little Chef</b>! Currently under construction.</p>
          <p>Built for HackHarvard 2018.</p>
          <div className="arrow"></div>
        </header>
        <div className="main">
          <Kitchen />
        </div>
      </div>
    );
  }
}

// ========================================

export default App;
