import React from 'react';
import logo from './little-chef.gif';
import './App.css';

function Add(props) {
  return (
    <div class="center">
      <button class="add-button hvr-bounce-in" type="button">+</button>
    </div>
  );
}

class Ingredient extends React.Component {
  render() {
    return (
      <div>
        {/* <h3>{desc}</h3> */}
        <input type="text"/>
      </div>
    );
  }
}

class Kitchen extends React.Component {
  renderIngredient(i) {
    return <Ingredient />;
  }

  renderAdd() {
    return <Add
      ingredients={this.props.ingredients}
    />;
  }

  render() {
    const emptyKitchen = "No ingredients added. You've got an empty kitchen!";
    return (
      <div>
        <div>
          (this.props.ingredients === 0) ?
            <h1>{emptyKitchen}</h1> :
            <h1>Not empty!!</h1>
        </div>
        <div>
          {this.renderAdd}
        </div>
      </div>
    );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ingredients: 0,
    }
  }

  render() {
    return(
      <div className="App">
        <header className="App-header">
          <h1>Little Chef</h1>
          <img src={logo} className="App-logo" alt="logo" />
          <p>Welcome to <b>Little Chef</b>! Currently under construction.</p>
          <p>Built for HackHarvard 2018.</p>
          <div className="arrow"></div>
        </header>
        <div className="main">
          <Kitchen
            ingredients={this.state.ingredients}
          />
        </div>
      </div>
    );
  }
}

// ========================================

export default App;
