import React from 'react';
import logo from './little-chef.gif';
import './App.css';

function Add(props) {
  return (
    <div className="center">
      <button className="add-button hvr-bounce-in noSelect" onClick={props.onClick} type="button">+</button>
    </div>
  );
}

class Ingredient extends React.Component {
  // handleIngredients() {
  //   const groceries = this.props.ingredients.map((item, num) => {
  //     const desc = num ? 'Ingredient #' + num : 'Nothing here';
  //     return (
  //       <div>
  //         <h3>{desc}</h3>
  //         <input type="text"/>
  //       </div>
  //     );
  //   });
  // }

  handleIngredients() {
    let table = []
    // Outer loop to create parent
    for (let i = 0; i < this.props.ingredients; i++) {
      let children = [];
      const desc = 'Ingredient #' + (i + 1);
      children.push(<div className="center">
                      <h3>{desc}</h3>
                      <input type="text"/>
                      <button className="minus-button hvr-bounce-out noSelect" onClick={this.props.onMinus} type="button">-</button>
                    </div>);
      table.push(<tr>{children}</tr>);
    }
    return table;
  }

  render() {
    return (
      <div>
        <table>
          {this.handleIngredients()}
        </table>
      </div>
    );
  }
}

class Kitchen extends React.Component {
  renderIngredients() {
    return <Ingredient
      ingredients={this.props.ingredients}
      onMinus={() => this.props.onMinus()}
    />;
  }

  renderAdd() {
    return <Add
      ingredients={this.props.ingredients}
      onClick={() => this.props.onClick()}
    />;
  }

  renderHeader() {
    const emptyKitchen =
      <div>
        <h1>"No ingredients added.</h1>
        <h2>You've got an empty kitchen!"</h2>
      </div>;
    return (
      (this.props.ingredients === 0) ?
      <h1>{emptyKitchen}</h1> : ((this.props.ingredients === 1) ?
                                <h1>{this.props.ingredients} ingredient.</h1> :
                                <h1>{this.props.ingredients} ingredients.</h1>)
    );
  }

  render() {
    return (
      <div>
        <div>
          {this.renderHeader()}
        </div>
        <div>
          {this.renderIngredients()}
        </div>
        <div>
          {this.renderAdd()}
        </div>
      </div>
    );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ingredients: 0,
    }
  }

  handleClick() {
    const addOne = this.state.ingredients + 1;
    this.setState({
      ingredients: addOne,
    });
  }

  handleMinus() {
    const minusOne = this.state.ingredients - 1;
    this.setState({
      ingredients: minusOne,
    });
  }

  render() {
    return(
      <div className="App">
        <header className="App-header">
          <h1>Little Chef</h1>
          <img src={logo} className="App-logo" alt="logo" />
          <p>Welcome to <b>Little Chef</b>! Currently under construction.</p>
          <p>Built for HackHarvard 2018.</p>
          <div className="arrow"></div>
        </header>
        <div className="main">
          <Kitchen
            ingredients={this.state.ingredients}
            onClick={() => this.handleClick()}
            onMinus={() => this.handleMinus()}
          />
        </div>
      </div>
    );
  }
}

// ========================================

export default App;
